f __init__(self, data_name): #data_name will be set 'train' or 'valid'(the folder names) self.dataname = data_name #trainíŒŒì¼ ê²½ë¡œë¦¬ìŠ¤íŠ¸ ìƒì„± self.img_path = [] #ê²½ë¡œê°€ .jpg í™•ì¥ìì¸ íŒŒì¼ë“¤ì„ img_pathì— ë¦¬ìŠ¤íŠ¸í™” ì‹œì¼œì¤Œ for name in class_names: self.img_path.append(glob.glob(f'./data/{data_name}/{name}/*jpg')) #2ì°¨ì› ë¦¬ìŠ¤íŠ¸ -&gt; 1ì°¨ì› ë¦¬ìŠ¤íŠ¸ self.img_path = sum(self.img_path, []) #trainíŒŒì¼ì˜ labels ìƒì„± self.labels = [] for path in self.img_path: self.labels.append(class_names.index(path.split('/')[3])) #í…ì„œíƒ€ì…ìœ¼ë¡œ ë³€ê²½í•˜ëŠ” ë³€ìˆ˜ ìƒì„± self.img_transpose = transforms.Compose([transforms.ToTensor()]) def __getitem__(self, index): img = Image.open(self.img_path[index]) # swin_base_patch4_window7_224ëª¨ë¸ì´ 224,224ë¡œ íŠ¸ë ˆì´ë‹ ëœ ëª¨ë¸ì´ë¼ì„œ # ì´ë¯¸ì§€ ë³´ê°„ë²•ì„ ì´ìš©í•˜ì—¬ (224,224)ì‚¬ì´ì¦ˆë¡œ ë³€ê²½ if img.size != (224,224): img = img.resize((224,224),Image.Resampling.BILINEAR) # Data augmentation with PIL when only 'train set' if self.dataname == 'train': if random.uniform(0,1) &lt; 0.3 or img.getbands() == 'L': img = img.convert('L').convert('RGB') # Random crop with size (64,64) from 30% if random.uniform(0,1) &lt; 0.3 : img = img.resize((224+64,224+64), Image.Resampling.BILINEAR) x = random.randrange(0,64) y = random.randrange(0,64) img = img.crop((x,y,x+224, y+224)) # Random Gaussian blur from 20% if random.uniform(0,1) &lt; 0.2: img = img.filter(ImageFilter.GaussianBlur(random.uniform(0.5,1.2))) # Random flip from 30% if random.uniform(0,1) &lt; 0.3: img = img.transpose(Image.Transpose.FLIP_LEFT_RIGHT) # ì™œì¸ì§€ ëª¨ë¥´ê² ì§€ë§Œ êµ¬ê¸€ì´ë¯¸ì§€ ì¤‘ ì»¬ëŸ¬ì¸ë° 1ì±„ë„ì´ ì¡´ì¬í•´ì„œ ë¬´ì¡°ê±´ 3ì±„ë„ë¡œ ë°”ê¿”ì¤˜ì•¼í•©ë‹ˆë‹¤. else : img = img.convert('RGB') lbl = self.labels[index] lbl = torch.tensor(lbl) img = self.img_transpose(img) return img, lbl #Sports_Dataset[0] == img, Sports_Dataset[1] == lbl(ë°ì´í„° ì‚¬ìš© ë°©ì‹) def __len__(self): return len(self.img_path) 1234567891011121314151617181920# ì´ë¯¸ì§€ 8ê°œ í™•ì¸ ë